# Define server logic required to draw a histogram
server <- function(input, output) {
    
    output$bodymapSVG <- renderUI({
        
        ## -- Config --
        ## When to turn things yellow
        yellowThreshold = 0.75
        ## Color Scheme
        wearableColor = "#00FF50"
        warningColor = "yellow"
        unWearableColor = "transparent"

        
        ## -- Hide or Show the regions for the data collection 
        wristVisibility = feetVisibility = headVisibility = "hidden"
        bicepVisibility = chestHeartVisibility = thighsVisibility = earsVisibility = fingertipsVisibility = "hidden"  
        
        footColor = wristColor = headColor = bicepColor = chestHeartColor = thighsColor = earsColor = fingertipsColor = wearableColor   
        if ("Heart monitoring" %in% input$biometrics) {
            bicepVisibility = chestHeartVisibility = thighsVisibility = earsVisibility = fingertipsVisibility = "visible"    
        }
        else if (length(input$biometrics) < 1) {
            wristVisibility = feetVisibility = headVisibility = "visible"
         
        }
        
        ## If it accepts touch interactions..
        if (input$touchInteractive) {
            ## .. hide the thighs and breast area
            thighsVisibility = "hidden"
            
            chestHeartVisibility = "hidden"
        }
        
        ## Default set, no data collection just a wearable
        
        ## -- Weight thresholds --
        if (input$weight < 0.5) {
            ## Almost everything is wearable at half a pound
        }
        else if (input$weight < 2.0) {
            earsColor = fingertipsColor = unWearableColor
        }
        else if (input$weight < 5.0) {
            earsColor = fingertipsColor = unWearableColor
        }
        else if (input$weight < 9.0) {
            earsColor = fingertipsColor = unWearableColor
            bicepColor = unWearableColor
        }
        else if (input$weight < 13.0) {
            headColor = unWearableColor
            earsColor = fingertipsColor = unWearableColor
            thighsColor = unWearableColor
            bicepColor = unWearableColor
        }
        else if (input$weight < 17.0) {
            headColor = unWearableColor 
            earsColor = fingertipsColor = unWearableColor
            thighsColor = unWearableColor
            bicepColor = unWearableColor
            chestHeartColor = unWearableColor
        }
        else {
            headColor = unWearableColor
            earsColor = fingertipsColor = unWearableColor
            thighsColor = unWearableColor
            bicepColor = unWearableColor
            chestHeartColor = unWearableColor
        }
        
        weightThreshold = 9
        if (input$weight >= weightThreshold) {
            headColor = unWearableColor    
        }
        else if (input$weight >= (weightThreshold * yellowThreshold) ) {
            headColor = warningColor    
        }
        
        ## Wrist
        weightThreshold = 0.5
        if (input$weight >= weightThreshold) {
            wristColor = unWearableColor    
        }
        else if (input$weight >= (weightThreshold * yellowThreshold) ) {
            wristColor = warningColor    
        }
        
        ## Feet
        weightThreshold = 5
        if (input$weight >= weightThreshold) {
            footColor = unWearableColor    
        }
        else if (input$weight >= (weightThreshold * yellowThreshold) ) {
            footColor = warningColor    
        }
        
        
        ## -- Color the regions --
        
        
        
        
        return(HTML(paste0(
